import { Draw, Tool, useFps } from 'react-native-drawing'
import { ReactElement, useRef } from 'react'
import { Text } from 'react-native'

const App = (): ReactElement => {

  const drawRef = useRef( null )
  const fps = useFps( drawRef )

  return (
    <>
      <Text>FPS: { fps }</Text>
      <Draw
        ref={ drawRef }
        resolution={ 720 }
        color="red"
        tool={ Tool.PENCIL } />
    </>
  )

}

export default App
